import { StyleSheet, Text, View } from "@bacons/react-views";
import { createURL } from "expo-linking";
import React from "react";

import { usePathname, useRouter } from "../hooks";
import { Link } from "../link/Link";
import { useNavigation } from "../useNavigation";

const useLayoutEffect =
  typeof window !== "undefined" ? React.useLayoutEffect : function () {};

function NoSSR({ children }: { children: React.ReactNode }) {
  const [render, setRender] = React.useState(false);
  React.useEffect(() => {
    setRender(true);
  }, []);

  if (!render) {
    return null;
  }

  return <>{children}</>;
}

/** Default screen for unmatched routes. */
export function Unmatched() {
  const router = useRouter();
  const navigation = useNavigation();
  const pathname = usePathname();
  const url = createURL(pathname);

  useLayoutEffect(() => {
    navigation.setOptions({
      title: "Not Found",
    });
  }, [navigation]);

  return (
    <View style={styles.container}>
      <Text
        accessibilityRole="header"
        accessibilityLevel={1}
        style={styles.title}
      >
        Unmatched Route
      </Text>
      <Text
        accessibilityRole="header"
        accessibilityLevel={2}
        style={styles.subtitle}
      >
        Page could not be found.{" "}
        <Text
          onPress={() => {
            if (router.canGoBack()) {
              router.back();
            } else {
              router.replace("/");
            }
          }}
          style={styles.link}
        >
          Go back.
        </Text>
      </Text>

      <NoSSR>
        <Link href={pathname} replace style={styles.link}>
          {url}
        </Link>
      </NoSSR>

      <Link href="/_sitemap" replace style={[styles.link, { marginTop: 8 }]}>
        Sitemap
      </Link>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "black",
    padding: 24,
    alignItems: "center",
    justifyContent: "center",
  },
  title: {
    color: "white",
    fontSize: 36,
    paddingBottom: 12,
    marginBottom: 12,
    borderBottomColor: "#323232",
    borderBottomWidth: 1,
    textAlign: "center",
    fontWeight: "bold",
  },
  subtitle: {
    color: "white",
    fontSize: 18,
    marginBottom: 12,
    textAlign: "center",
  },
  link: { color: "rgba(255,255,255,0.4)", textAlign: "center" },
});
